<template lang="pug">
   .nav
      .header
         .brand
            router-link(to="/")
               img(src="../../assets/static/logo.png", class="favicon")
               span.text aHnaine.dev
         .links
            ul
               router-link(to="/me", tag="li", exact)
                  a About
               router-link(to="/blog", tag="li")
                  a Articles
               router-link(to="/newsletter", tag="li")
                  a Newsletter
         .swt
            button(@click="changeTheme" class="light-switcher")
               img(class="theme-color" id="svg-chng" src="data:image/svg+xml;base64,")

</template>

<script>
export default {
   name: "Header",
   methods: {
      handleSCroll() {
         let header = document.querySelector(".nav");
         if (window.scrollY > 5) {
            header.className = "nav nav-scroll";
         } else {
            header.className = "nav";
         }
      },
      changeTheme() {
         let x = document.getElementById("svg-chng");
         let mainBody = document.getElementsByTagName("BODY")[0];
         let eleNav = document.getElementById("nav");

         if (mainBody.className == "dark") {
            x.src =
               "data:image/svg+xml;base64,";
         } else {
            x.src =
               "data:image/svg+xml;base64,";
         }

         mainBody.classList.toggle("dark");
         eleNav.classList.toggle("dark");
      }
   },
   created() {
      window.addEventListener("scroll", this.handleSCroll);
   },
   destroyed() {
      window.removeEventListener("scroll", this.handleSCroll);
   }
};
</script>
